dynamic_elements.chart_query:
SELECT DISTINCT ?x (COUNT(DISTINCT ?content) AS ?y) 
WHERE { 
?content <https://schema.org/additionalType> ?type. 
?type rdfs:label ?x .
 } GROUP BY ?x ?y

dynamic_elements.textsearch_query:
SELECT DISTINCT ?item ?itemLabel ?country ?countryLabel WHERE {
  SERVICE wikibase:mwapi {
      bd:serviceParam wikibase:endpoint "www.wikidata.org";
                      wikibase:api "EntitySearch";
                      mwapi:search "<<searchterm>>";
                      mwapi:language "en".
      ?item wikibase:apiOutputItem mwapi:item.
  }
  ?item (wdt:P279|wdt:P31) wd:Q207694 ; wdt:P17 ?country .
SERVICE wikibase:label { 
    bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en"
  }
}  LIMIT 20

dynamic_elements.tablevalueaction_query:
SELECT DISTINCT ?museum ?museumLabel 
WHERE { 
  <<item>> wdt:P17 ?country . 
  ?museum (wdt:P279|wdt:P31) wd:Q33506; wdt:P17 ?country .
SERVICE wikibase:label { 
    bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en"
  }
}
LIMIT 10

dynamic_elements.count_query:
PREFIX led: <http://led.kmi.open.ac.uk/term/> 
PREFIX event: <http://purl.org/NET/c4dm/event.owl#>
PREFIX dc: <http://purl.org/dc/terms/>
PREFIX schema: <http://schema.org/>
SELECT DISTINCT (COUNT(?nurse) AS ?count) 
WHERE {
VALUES ?nocc { 
      <http://data.open.ac.uk/led/occupation/nurse> 
      <http://data.open.ac.uk/led/thing/QAIMNS+nurse> 
      <http://data.open.ac.uk/led/thing/VAD+nurse> 
   }
   . ?nurse <http://dbpedia.org/ontology/occupation> ?nocc .
}
