dynamic_elements.chart_query:
SELECT DISTINCT ?label (COUNT(DISTINCT ?content) AS ?count) 
WHERE { 
?content <https://schema.org/additionalType> ?type. 
?type rdfs:label ?label .
 } GROUP BY ?label ?count

dynamic_elements.textsearch_query:
SELECT DISTINCT ?item ?itemLabel ?country ?countryLabel WHERE {
  SERVICE wikibase:mwapi {
      bd:serviceParam wikibase:endpoint "www.wikidata.org";
                      wikibase:api "EntitySearch";
                      mwapi:search "<<searchterm>>";
                      mwapi:language "en".
      ?item wikibase:apiOutputItem mwapi:item.
  }
  ?item (wdt:P279|wdt:P31) wd:Q207694 ; wdt:P17 ?country .
SERVICE wikibase:label { 
    bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en"
  }
}  LIMIT 20

